import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};
//로그인 액션 크리에이터 함수
export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
//로그아웃 액션 크리에이터 함수
export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  };
};
//async action creator : redux saga

//action creator
const changeNickname = (data) => {
  return {
    type: "CHANGE_NICKNAME",
    data,
  };
};
//액션함수로 액션 만들기
changeNickname("chuchu");
// {
//     type : "CHANGE_NICKNAME",
//     data : 'chuchu'
// }

//만들어낸 액션은 디스패치로 컴포넌트에서 사용하기
// store.dispatch(changeNickname('shushu'))

//(이전 상태 , 액션) => 다음 상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
          user: null,
        },
      };
  }
};

export default rootReducer;
