import { Card } from "antd";
import React from "react";

const PostCard = ({post}) => {
  return (
    <div>
      <Card
        cover={postMessage.Images[0]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
