import React from "react";
import { Card } from "antd";
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  Popover,
  EllipsisOutlined,
} from "@ant-design";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={postMessage.Images[0] && <PostImages images={post.Images} />}
        actions={[ //배열 안의 jsx에는 key가 필요
          <RetweetOutlined key="retweet"/>,
          <HeartOutlined key="heart" />,
          <MessageOutlined key="comment" />,
          <Popover key="more">
            <EllipsisOutlined />
          </Popover>,
        ]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
