//getInitialProps가 거의 안쓰이고 getStatic / getserverside Props로 바뀜 
import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

//async action creator : redux saga

//action creator
const changeNickname = (data) => {
  return {
    type: "CHANGE_NICKNAME",
    data,
  };
};
//액션함수로 액션 만들기
changeNickname("chuchu");
// {
//     type : "CHANGE_NICKNAME",
//     data : 'chuchu'
// }

//만들어낸 액션은 디스패치로 컴포넌트에서 사용하기
// store.dispatch(changeNickname('shushu'))

//(이전 상태 , 액션) => 다음 상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE:
      console.log("HYDRATE", action);
      return { ...state, ...action.payload };
    case "LOG_IN":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
          user: null,
        },
      };

    default:
      return state;//없으면 초기화시 리턴값이 언디파인드가 뜨므로 필요
  }
};

export default rootReducer;
